import React from 'react';
import './CSS/upload.css'
import axios, { post } from 'axios'
var cryptoJs = require('crypto-js');
const hostname = window.location.hostname;


export class UploadPage extends React.Component {

  constructor(props){
    super(props);

    this.state={
        video:'',
        thum:''
    }

  }

  video = (e) => {
    var file = e.target.files

    let reader = new FileReader();
    reader.readAsDataURL(file[0])

    reader.onload = (e) => {
        this.setState({video:e.target.result})
    }
  }

  thum = (e) => {
      var file = e.target.files

      let reader = new FileReader();
      reader.readAsDataURL(file[0])

      reader.onload = (e) => {
          this.setState({thum:e.target.result})
      }
  }

  upload = () => {

  }


  render(){
    return(
        <div className="uploadPageWrapper">
            <h1>Upload</h1>
            <h1>Video</h1>
            <input type='file' id='videoInput' onChange={(e)=>this.video(e)}/>
            <h1>Thum</h1>
            <input type='file' id='thumInput' onChange={(e)=>this.thum(e)}/>
            <input id='titleInput' placeholder='Title'/>
            <input id='desInput' placeholder='Description'/>

            <button onClick={()=>{this.upload()}}>Upload</button>
        </div>
    )
  }
}
