import React from 'react';
import './CSS/login.css'
var dotenv = require('dotenv')
dotenv.config();
var cryptoJs = require('crypto-js');
const hostname = window.location.hostname;


export class LoginPage extends React.Component {

  constructor(props){
    super(props);

    this.state={
    }

  }

  register = () => {
    var email = document.getElementById('emailinput').value
    var username = document.getElementById('usernameinput').value
    var pass = document.getElementById('passwordinput').value
    var passwordconfirm  = document.getElementById('passwordconfirminput').value



    var message = {username:username, password : pass, passwordconfirm : passwordconfirm , email : email}
    var cipher = cryptoJs.AES.encrypt(JSON.stringify(message),process.env.SECRET_TOKEN_CODE.toString());
    console.log(process.env.SECRET_TOKEN_CODE.toString())
    console.log(cipher)
  }


  render(){
    return(
        <div className="loginPageWrapper">
          <div className="registerBox">
            <input className="registerInputs" id='emailinput' placeholder='Email'/>
            <input className="registerInputs" id='usernameinput' placeholder='Username'/>
            <input className="registerInputs" id='passwordinput' placeholder='Password' type='password'/>
            <input className="registerInputs" id='passwordconfirminput' placeholder='Confirm Password' type='password'/>
            <button className="regButton" onClick={()=>{this.register()}}>Register</button>
          </div>
        </div>
    )
  }
}
