import React from 'react';
import './CSS/login.css'
const hostname = window.location.hostname;


export class LoginPage extends React.Component {

  constructor(props){
    super(props);

    this.state={
    }

  }

  register = () => {
    const requestOptions = {
      method : 'POST',
      headers : { 'Content-Type' : 'application/json'},
      body: JSON.stringify({ title: 'POST EXAMPLES TEST THINGY'})
    }
    fetch(`http://${hostname}:4000/api/register`, requestOptions)
    .then(response => response.json())
    .then(data => {
      console.log(data)
    })
  }


  render(){
    return(
        <div className="loginPageWrapper">
          <div className="registerBox">
            <input className="registerInputs" placeholder='Email'/>
            <input className="registerInputs" placeholder='Username'/>
            <input className="registerInputs" placeholder='Password' type='password'/>
            <input className="registerInputs" placeholder='Confirm Password' type='password'/>
            <button onClick={()=>{this.register()}}>Register</button>
          </div>
        </div>
    )
  }
}
