import React from 'react';
import './CSS/login.css'
var cryptoJs = require('crypto-js');
const hostname = window.location.hostname;


export class LoginPage extends React.Component {

  constructor(props){
    super(props);

    this.state={
    }

  }

  register = () => {
    var email = document.getElementById('emailinput').value
    var username = document.getElementById('usernameinput').value
    var password = document.getElementById('passwordinput').value
    var passwordconfirm  = document.getElementById('passwordconfirminput').value
    var message = {username:username,email:email,password:password,confirmpassword:passwordconfirm}
    var cipher = cryptoJs.AES.encrypt(JSON.stringify(message),process.env.REACT_APP_SECRET_TOKEN_CODE).toString();
    cipher = cipher.replace(/[/]/g,'★')
    cipher = cipher.replace(/[+]/g,'☆')
    fetch(`http://${hostname}:4000/api/register?data=${cipher}`)
    .then(response => response.json())
    .then(data => {
      console.log(data)
    })
  }


  login = () => {
    var username = document.getElementById('usernameLoginInput').value
    var password = document.getElementById('passwordLoginInput').value
    var message = {username:username,password:password}
    var cipher = cryptoJs.AES.encrypt(JSON.stringify(message),process.env.REACT_APP_SECRET_TOKEN_CODE).toString();
    cipher = cipher.replace(/[/]/g,'★')
    cipher = cipher.replace(/[+]/g,'☆')
    fetch(`http://${hostname}:4000/api/login?data=${cipher}`)
    .then(response => response.json())
    .then(data => {
      console.log(data.data)
      document.cookie = `token=${data.data};`
    })

  }


  render(){
    return(
        <div className="loginPageWrapper">
            <div className="registerDiv">
              <h1 className="regTitle">Register</h1>
              <input className="registerInputs" id='emailinput' placeholder='Email'/>
              <input className="registerInputs" id='usernameinput' placeholder='Username'/>
              <input className="registerInputs" id='passwordinput' placeholder='Password' type='password'/>
              <input className="registerInputs" id='passwordconfirminput' placeholder='Confirm Password' type='password'/>
              <button className="regButton" onClick={()=>{this.register()}}>Register</button>
            </div>
            <div className="loginDiv">
              <h1 className="loginTitle">Login</h1>
              <input className="loginInputs" id='usernameLoginInput' placeholder='Username'/>
              <input className="loginInputs" id='passwordLoginInput' placeholder='Password' type='password'/>
              <button className="logButton" onClick={() => {this.login()}}>Login</button>
            </div>
        </div>
    )
  }
}
