import React from 'react';
import './CSS/profile.css'
const hostname = window.location.hostname;




export class ProfilePage extends React.Component {

  constructor(props){
    super(props);

    this.state = {
        profile : ""
    }


    this.InitialiseUser.bind(this);
    this.InitialiseUser();
  }
  InitialiseUser(){

    let nameFromCookie = decodeURIComponent(document.cookie);
    nameFromCookie = nameFromCookie.split('=');
    let username = nameFromCookie[1];
    if(username != '' || username != undefined){
        fetch(`http://localhost:4000/api/profile?name=${username}`)
        .then(response => response.json())
        .then((data)=>{
            this.setState({ profile : data.data[0]})
            document.getElementById('avatar').src=`${this.state.profile.avatar}`
            this.getCompleted()
        })
    }else{
        return document.location.href='http://localhost:3000/'
        }
    }


    getCompleted = () => {
        fetch(`http://localhost:4000/api/getcompleted?uid=${this.state.profile.id}`)
        .then(response => response.json())
        .then((data)=>{
            for(let i=0;i<data.data.length;i++){
                fetch(`http://localhost:4000/api/getquestions?id=${data.data[i].id}`)
            }
        })
    }

  render(){
    return(
      <div className="profilePageWrapper">
          <h2 id='username'>{this.state.profile.username}</h2>
          <img id='avatar'/>

          <div className='completedChallenges'>
            <h4 id='completedchallengestitle'>Completed Challenges</h4>
            <div id='showchallenges'>

            </div>
          </div>
      </div>
    )
  }
}
