import React from 'react';
import './CSS/upload.css'
var cryptoJs = require('crypto-js');
const hostname = window.location.hostname;


export class UploadPage extends React.Component {

  constructor(props){
    super(props);

    this.state={
    }

  }

  upload = (e) => {
      var file = e.target.files

      let reader = new FileReader();
      reader.readAsDataURL(file[0])

      reader.onload = (e) => {
          console.log(e.target.result)
          var message = {data:e.target.result,token:process.env.REACT_APP_SECRET_TOKEN_CODE}
          var cipher = cryptoJs.AES.encrypt(JSON.stringify(message),process.env.REACT_APP_SECRET_TOKEN_CODE).toString();
          cipher = cipher.replace(/[/]/g,'★')
          cipher = cipher.replace(/[+]/g,'☆')
          fetch(`http://${hostname}:4000/api/upload?data=${cipher}`)
          .then(response => response.json())
          .then(data => {
              console.log(data)
          })
      }

      console.log(file)
  }


  render(){
    return(
        <div className="uploadPageWrapper">
            <h1>Upload</h1>
            <input type='file' id='fileInput' onChange={(e)=>this.upload(e)}/>
        </div>
    )
  }
}
