import React from 'react';
import './CSS/App.css'
const hostname = window.location.hostname;


export class MainPage extends React.Component {

  constructor(props){
    super(props);

    this.state={
      sidebar: true
    }

    for(let i=1;i<=10;i++){
      fetch(`http://${hostname}:4000/api/getvideo?id=${i}`)
      .then(responce => responce.json())
      .then(data =>{
        console.log(data.result[0])
        if(data.result[0]){
          document.getElementById('feedwrapper').innerHTML += `<div class='feedvideo'><img id='videoimg' src='${data.result[0].videothum}'/><h1 id='videotitle'>${data.result[0].videoname}</h1><p id='videodes'>${data.result[0].videodes}</p></div>`
        }
      })
    }
  }


  togglesidebar = () => {
    if(this.state.sidebar == false){
      document.getElementById('sidebar').style.left = '-15%';
      document.getElementById('feedwrapper').style.width = '95%';
      this.setState({sidebar : true})
    }
    else{
      document.getElementById('sidebar').style.left = '0%';
      document.getElementById('feedwrapper').style.width = '85%';
      this.setState({sidebar : false})
    }
  }


  search = (a) => {
    console.log(a)
    fetch(`http://${hostname}:4000/api/search?input=${a}`)
    .then(response => response.json())
    .then(data => {
      console.log(data)
      var searchresultsdiv = document.getElementById('searchResults');

      if(a == ''){
        searchresultsdiv.innerHTML = '';
        searchresultsdiv.style.display = 'none';
        return false;
      }
      searchresultsdiv.innerHTML = '';

      if(data.data[0]){
        searchresultsdiv.style.display='inline-block';
        for(let i=0;i<data.data.length;i++){
          searchresultsdiv.innerHTML += `<div id='searchresultdiv'> <h3 id="searchresulttitle">${data.data[i].videoname}</h3> <p id="searchresultdis">${data.data[i].videodes}</p></div>`
        }
      }else{
        searchresultsdiv.style.display = 'hidden';
        searchresultsdiv.innerHTML = '';
      }
    })
  }

  /*


                      -------  ADD TO HTML  ------

              1. Add subscribers to sidebar from database - add backend aswell


  */

  render(){
    return(
      <div className="trendingWrapper">

    </div>
    )
  }
}
