    }

  }

  upload = (e) => {
      var file = e.target.files

      let reader = new FileReader();
      reader.readAsDataURL(file[0])

      reader.onload = (e) => {
          fetch(`http://${hostname}:4000/api/upload?data=${e.target.result}`)
          .then(response => response.json())
          .then(data => {
              console.log(data)
          })
      }

      console.log(file)
  }


  render(){
    return(
        <div className="uploadPageWrapper">
            <h1>Upload</h1>
            <input type='file' id='fileInput' onChange={(e)=>this.upload(e)}/>
        </div>
    )
  }
}
